;(function($){$.fn.simpleSlideshow=function(options){var config={cycleSpeed:5000,fadeSpeed:800,zIndex:999};if(options)$.extend(config,options);this.each(function(){var $container=$(this);function init(){$container.css({position:'relative',zIndex:config.zIndex});$('img',$container).hide();$('img:first',$container).show();} function cycle(){var next=$('img:visible',$container).next('img');if(!next.length){next=$('img:first',$container);} $('img',$container).css({zIndex:config.zIndex});var old=$('img:visible',$container);next.css({position:'absolute',left:0,top:0,zIndex:config.zIndex+1}).fadeIn(config.fadeSpeed,function(){old.hide();$(this).css({position:'static'});});} init();setInterval(cycle,config.cycleSpeed);});return this;};})(jQuery);
